```scss
class Node:
    def __init__(self, value, next, previous):
        self.value = value
        self.next = next
        self.previous = previous

class DoubleLinkedList:
    def __init__(self):
        self.head = None
        self.tail = None

    def add(self, value):
        new_node = Node(value, None, None)

        if self.head is None:
            self.head = new_node
