        stringstream ss;
        ss << requestUrl << "&" << postBody;
        string requestResult = ss.str();
        vector<string> urlRequests = split(requestResult, "&");
        httpClient http;
        http.POST(urlRequests[0]);
        stringstream ssResponse;
        ssResponse << http.getResponseBody();
        response = ssResponse.str();
    } catch (const exception& e) {
        cout << "Error: " << e.what() << endl;
    }

    return response;
}

int main() {
    // Get the user's search query from the console
    string query;
    cin >> query;

    // Perform the search
    string searchResult = search(query);

    // Print the search results to the console
