```
let bufferSize = 1024; // size of the buffer in samples
let buffer = []; // the buffer array

function pushAudio(audioData) {
  // if the buffer is full, shift the oldest samples to make room for the new ones
  if (buffer.length === bufferSize) {
    buffer = buffer.slice(1); // shift the oldest samples to the left
  }
  // add the new audio data to the buffer
  buffer.push(audioData);
}

function getBufferedAudio() {
  // if the buffer is not empty, return the oldest audio data
