use std::fs::File;
use std::io::{BufReader, BufRead};
use std::str::from_utf8;

fn count_gc_content(file_path: &str) -> u32 {
    let mut total_gc = 0;
    let mut seqs = vec![];

    let file = File::open(file_path).unwrap();
    let reader = BufReader::new(file);

    for line in reader.lines() {
        let seq = line.unwrap().split('\n').nth(0).unwrap();
        let seq = from_utf8(seq).unwrap();
        let gc_content = seq.chars().filter(|&c| c.is_gc()).count();
        total_gc += gc_content;
        seqs.push(seq);
    }

    total_gc
}

