// Define the color struct
typedef struct color {
    int r;
    int g;
    int b;
} color;

// Function to find the closest color in an array
int find_closest_color(color array[], int num_colors, color target) {
    // Initialize variables for closest color and distance
    int closest_index = -1;
    float closest_distance = 100;

    // Iterate through each color in the array
    for (int i = 0; i < num_colors; i++) {
        // Calculate the distance between the target color and the current color
        float distance = sqrt(pow(target.r - array[i].r, 2) + pow(target.g - array[i].g, 2) + pow(target.b - array[i].b, 2));

        // If the current color is closer than the current closest color, update the variables
        if (distance < closest_distance) {
            closest_index = i;
            closest_distance = distance;
        }
    }

    // Return the index of the closest color
    return closest_index;
}

int main() {
    // Define an array of colors
    color array[7] = {
        {0, 0, 0},
        {255, 0, 0},
        {0, 255, 0},
        {0, 0, 255},
        {255, 255, 0},
        {255, 0, 255},
        {0, 255, 255}
    };

    // Define a target color
    color target = {128, 128, 128};

    // Find the index of the closest color in the array
    int closest_index = find_closest_color(array, 7, target);

    // Print the result
    printf("The closest color to %d, %d, %d is index %d\n", target.r, target.g, target.b, closest_index);

    return 0;
}
